mapper.select:
select

from fruit
where 1=1

    and  fruit_type = #{fruitType,jdbcType=INTEGER}


    and fruit_name like "%"#{fruitName,jdbcType=VARCHAR}"%"

limit #{start,jdbcType=INTEGER},#{size,jdbcType=INTEGER}

select count(id) from fruit where 1=1
and fruit_type = #{fruitType,jdbcType=INTEGER}
and fruit_name like "%"#{fruitName,jdbcType=VARCHAR}"%"
